import json
import argparse

def clean_event(event):
    # 去除 timestamp、user_id、session_id、model 字段
    for k in ["timestamp", "user_id", "session_id", "model"]:
        if k in event:
            event.pop(k)

    # 只处理 metadata 字段，去除 cli_version/platform/working_directory
    if 'metadata' in event and isinstance(event['metadata'], dict):
        new_meta = {k: v for k, v in event['metadata'].items() if k not in ("cli_version", "platform", "working_directory")}
        if new_meta:
            event['metadata'] = new_meta
        else:
            event.pop('metadata')

    # 转换event_type为role，调整字段名称
    if 'event_type' in event:

        event_type = event.pop('event_type')
        if event_type == 'user_query':
            event['role'] = 'user'
            if 'query' in event:
                event['content'] = event.pop('query')
        elif event_type == 'assistant_response':
            event['role'] = 'assistant'
            if 'response' in event:
                event['content'] = event.pop('response')
        else:
            # 保持其他类型的event_type不变
            event['role'] = event_type

    return event

def process_file(input_path):
    with open(input_path, 'r', encoding='utf-8') as f:
        data = json.load(f)
    # 逐条处理
    cleaned = [clean_event(dict(event)) for event in data]

    # 进行转换
    transformed = []
    i = 0
    n = len(cleaned)
    while i < n:
        ev = cleaned[i]
        if ev.get('role') == 'tool_call':
            # 收集连续的 tool_call
            tool_calls = []
            while i < n and cleaned[i].get('role') == 'tool_call':
                tc = cleaned[i]
                call_entry = {}
                call_entry['id'] = tc.get('tool_call_id') or tc.get('tool_call') or None
                meta = tc.get('metadata') if isinstance(tc.get('metadata'), dict) else {}
                call_entry['type'] = meta.get('tool_type') or 'function'
                func = {}
                func['name'] = tc.get('tool_name') or tc.get('tool') or tc.get('function') or None
                args_raw = tc.get('tool_args')
                func_args = None
                if isinstance(args_raw, dict):
                    func_args = args_raw
                elif isinstance(args_raw, str):
                    try:
                        func_args = json.loads(args_raw)
                    except Exception:
                        func_args = {'raw': args_raw}
                else:
                    func_args = args_raw
                func['arguments'] = func_args
                call_entry['function'] = func
                tool_calls.append(call_entry)
                i += 1

            assistant_entry = {
                'role': 'assistant',
                'content': '',
                'tool_calls': tool_calls,
            }
            transformed.append(assistant_entry)
            continue

        if ev.get('role') == 'tool_result':
            tr = cleaned[i]
            tool_entry = {
                'role': 'tool',
                'content': tr.get('tool_result') if tr.get('tool_result') is not None else tr.get('tool_result_text', ''),
                'tool_call_id': tr.get('tool_call_id') or tr.get('tool_call') or None,
            }
            transformed.append(tool_entry)
            i += 1
            continue

        transformed.append(ev)
        i += 1

    return transformed

def main():
    parser = argparse.ArgumentParser(description="处理单个文件或目录中的所有JSON文件")
    parser.add_argument('input', help='输入JSON文件或目录')
    parser.add_argument('-o', '--output', required=True, help='输出JSON文件')
    parser.add_argument('--pattern', default='*.json', help='当输入为目录时的文件匹配模式，默认为*.json')
    args = parser.parse_args()

    import os
    import glob

    all_transformed = []

    if os.path.isdir(args.input):
        # 如果输入是目录，处理目录下所有匹配的文件
        pattern = os.path.join(args.input, args.pattern)
        input_files = sorted(glob.glob(pattern))
        if not input_files:
            print(f"警告：在目录 {args.input} 中没有找到匹配 {args.pattern} 的文件")
            return

        for file_path in input_files:
            print(f"处理文件：{file_path}")
            try:
                transformed = process_file(file_path)
                # 每个文件的内容用{}包装
                all_transformed.append({
                    "source_file": file_path,
                    "content": transformed
                })
            except Exception as e:
                print(f"处理文件 {file_path} 时出错：{str(e)}")
                continue
    else:
        # 如果输入是单个文件
        try:
            transformed = process_file(args.input)
            all_transformed.append({
                "source_file": args.input,
                "content": transformed
            })
        except Exception as e:
            print(f"处理文件时出错：{str(e)}")
            return
    # 逐条处理
    cleaned = [clean_event(dict(event)) for event in data]

    # 把连续的 tool_call 事件聚合为一个 assistant 对象，形如:
    # {"role":"assistant","content":"","tool_calls":[{...},{...}]}
    transformed = []
    i = 0
    n = len(cleaned)
    while i < n:
        ev = cleaned[i]
        # 在 clean_event 之后，原始 event_type=="tool_call" 会变成 role=="tool_call"
        if ev.get('role') == 'tool_call':
            # 收集连续的 tool_call
            tool_calls = []
            while i < n and cleaned[i].get('role') == 'tool_call':
                tc = cleaned[i]
                # 构造 tool_call 条目
                call_entry = {}
                call_entry['id'] = tc.get('tool_call_id') or tc.get('tool_call') or None
                # 类型优先从 metadata.tool_type，然后回退到 'function'
                meta = tc.get('metadata') if isinstance(tc.get('metadata'), dict) else {}
                call_entry['type'] = meta.get('tool_type') or 'function'
                func = {}
                func['name'] = tc.get('tool_name') or tc.get('tool') or tc.get('function') or None
                # 尝试把 tool_args 解析成 dict
                args_raw = tc.get('tool_args')
                func_args = None
                if isinstance(args_raw, dict):
                    func_args = args_raw
                elif isinstance(args_raw, str):
                    try:
                        func_args = json.loads(args_raw)
                    except Exception:
                        # 保留原始字符串作为 arguments 的一个字段
                        func_args = {'raw': args_raw}
                else:
                    func_args = args_raw
                func['arguments'] = func_args
                call_entry['function'] = func
                tool_calls.append(call_entry)
                i += 1

            # 将聚合后的 tool_calls 放入一个 assistant 消息
            assistant_entry = {
                'role': 'assistant',
                'content': '',
                'tool_calls': tool_calls,
            }
            transformed.append(assistant_entry)
            continue

        # 把 tool_result 转换为 role=="tool" 结构，包含 tool_call_id 和 content
        if ev.get('role') == 'tool_result':
            tr = cleaned[i]
            tool_entry = {
                'role': 'tool',
                'content': tr.get('tool_result') if tr.get('tool_result') is not None else tr.get('tool_result_text', ''),
                'tool_call_id': tr.get('tool_call_id') or tr.get('tool_call') or None,
            }
            transformed.append(tool_entry)
            i += 1
            continue

        # 非 tool_call 事件，直接保留
        transformed.append(ev)
        i += 1

    with open(args.output, 'w', encoding='utf-8') as f:
        json.dump(transformed, f, ensure_ascii=False, indent=2)

if __name__ == '__main__':
    main()
