import numpy as np
from typing import List, Dict, Any, Optional
from datetime import datetime, timedelta
from collections import Counter, defaultdict
import json

from models import PlannerRecord, AnalysisFilter, PlannerStatus
from database import PlannerDatabase

class PlannerAnalyzer:
    """Planner 数据分析器"""

    def __init__(self, db_path: str = "planner_records.db"):
        self.db = PlannerDatabase(db_path)

    def analyze_failure_patterns(self, limit: int = 100) -> Dict[str, Any]:
        """分析失败模式"""
        failure_records = self.db.get_failure_records(limit)
        error_records = self.db.get_error_records(limit)

        # 合并失败和错误记录
        bad_records = failure_records + error_records

        if not bad_records:
            return {"message": "没有找到失败或错误记录"}

        # 分析错误消息模式
        error_messages = [r.error_message for r in bad_records if r.error_message]
        error_patterns = self._extract_error_patterns(error_messages)

        # 分析任务描述中的关键词
        task_descriptions = [r.input_data.task_description for r in bad_records]
        task_keywords = self._extract_keywords(task_descriptions)

        # 分析执行时间分布
        execution_times = [r.execution_time for r in bad_records if r.execution_time]
        time_stats = self._calculate_time_stats(execution_times)

        # 按会话分组分析
        session_failures = defaultdict(list)
        for record in bad_records:
            session_failures[record.session_id].append(record)

        return {
            "total_bad_records": len(bad_records),
            "error_patterns": error_patterns,
            "task_keywords": task_keywords,
            "execution_time_stats": time_stats,
            "session_failure_analysis": {
                "total_sessions": len(session_failures),
                "avg_failures_per_session": len(bad_records) / len(session_failures) if session_failures else 0,
                "worst_sessions": sorted(
                    [(sid, len(records)) for sid, records in session_failures.items()],
                    key=lambda x: x[1], reverse=True
                )[:5]
            },
            "recent_failures": [
                {
                    "id": r.id,
                    "task": r.input_data.task_description[:100] + "...",
                    "error": r.error_message[:100] + "..." if r.error_message else "No error message",
                    "created_at": r.created_at.isoformat()
                }
                for r in sorted(bad_records, key=lambda x: x.created_at, reverse=True)[:10]
            ]
        }

    def analyze_success_patterns(self, limit: int = 100) -> Dict[str, Any]:
        """分析成功模式"""
        filter_obj = AnalysisFilter(status=PlannerStatus.SUCCESS)
        success_records = self.db.get_records(filter_obj, limit=limit)

        if not success_records:
            return {"message": "没有找到成功记录"}

        # 分析置信度分布
        confidence_scores = [r.output_data.confidence_score for r in success_records 
                           if r.output_data and r.output_data.confidence_score is not None]

        # 分析计划步骤数量
        step_counts = [len(r.output_data.plan_steps) for r in success_records 
                      if r.output_data and r.output_data.plan_steps]

        # 分析任务类型
        task_descriptions = [r.input_data.task_description for r in success_records]
        task_types = self._classify_task_types(task_descriptions)

        return {
            "total_success_records": len(success_records),
            "confidence_stats": {
                "mean": np.mean(confidence_scores) if confidence_scores else None,
                "median": np.median(confidence_scores) if confidence_scores else None,
                "min": min(confidence_scores) if confidence_scores else None,
                "max": max(confidence_scores) if confidence_scores else None
            },
            "step_count_stats": {
                "mean": np.mean(step_counts) if step_counts else None,
                "median": np.median(step_counts) if step_counts else None,
                "min": min(step_counts) if step_counts else None,
                "max": max(step_counts) if step_counts else None
            },
            "task_types": task_types
        }

    def compare_success_vs_failure(self) -> Dict[str, Any]:
        """对比成功和失败案例"""
        success_records = self.db.get_records(AnalysisFilter(status=PlannerStatus.SUCCESS), limit=50)
        failure_records = self.db.get_records(AnalysisFilter(status=PlannerStatus.FAILURE), limit=50)

        # 对比任务描述长度
        success_lengths = [len(r.input_data.task_description) for r in success_records]
        failure_lengths = [len(r.input_data.task_description) for r in failure_records]

        # 对比执行时间
        success_times = [r.execution_time for r in success_records if r.execution_time]
        failure_times = [r.execution_time for r in failure_records if r.execution_time]

        # 对比约束条件数量
        success_constraints = [len(r.input_data.constraints or []) for r in success_records]
        failure_constraints = [len(r.input_data.constraints or []) for r in failure_records]

        return {
            "task_description_length": {
                "success": {"mean": np.mean(success_lengths), "median": np.median(success_lengths)},
                "failure": {"mean": np.mean(failure_lengths), "median": np.median(failure_lengths)}
            },
            "execution_time": {
                "success": {"mean": np.mean(success_times), "median": np.median(success_times)} if success_times else None,
                "failure": {"mean": np.mean(failure_times), "median": np.median(failure_times)} if failure_times else None
            },
            "constraint_count": {
                "success": {"mean": np.mean(success_constraints), "median": np.median(success_constraints)},
                "failure": {"mean": np.mean(failure_constraints), "median": np.median(failure_constraints)}
            }
        }

    def get_trend_analysis(self, days: int = 7) -> Dict[str, Any]:
        """获取趋势分析"""
        end_date = datetime.now()
        start_date = end_date - timedelta(days=days)

        # 获取指定时间范围内的所有记录
        filter_obj = AnalysisFilter(start_date=start_date, end_date=end_date)
        records = self.db.get_records(filter_obj, limit=1000)

        # 按日期分组
        daily_stats = defaultdict(lambda: {"success": 0, "failure": 0, "error": 0, "total": 0})

        for record in records:
            date_key = record.created_at.date().isoformat()
            daily_stats[date_key]["total"] += 1
            daily_stats[date_key][record.status.value] += 1

        # 计算成功率趋势
        success_rates = []
        dates = sorted(daily_stats.keys())

        for date in dates:
            stats = daily_stats[date]
            success_rate = stats["success"] / stats["total"] if stats["total"] > 0 else 0
            success_rates.append({
                "date": date,
                "success_rate": success_rate,
                "total": stats["total"],
                "success": stats["success"],
                "failure": stats["failure"],
                "error": stats["error"]
            })

        return {
            "period": f"Last {days} days",
            "daily_trends": success_rates,
            "overall_success_rate": sum(r["success"] for r in success_rates) / sum(r["total"] for r in success_rates) if success_rates else 0
        }

    def export_bad_examples(self, output_file: str = "bad_examples.json", limit: int = 50) -> str:
        """导出坏例子到文件"""
        failure_records = self.db.get_failure_records(limit)
        error_records = self.db.get_error_records(limit)

        bad_examples = []

        for record in failure_records + error_records:
            example = {
                "id": record.id,
                "session_id": record.session_id,
                "input": {
                    "task_description": record.input_data.task_description,
                    "context": record.input_data.context,
                    "constraints": record.input_data.constraints,
                    "user_preferences": record.input_data.user_preferences
                },
                "output": record.output_data.model_dump() if record.output_data else None,
                "status": record.status.value,
                "error_message": record.error_message,
                "execution_time": record.execution_time,
                "created_at": record.created_at.isoformat(),
                "tags": record.tags
            }
            bad_examples.append(example)

        with open(output_file, 'w', encoding='utf-8') as f:
            json.dump(bad_examples, f, ensure_ascii=False, indent=2)

        return f"已导出 {len(bad_examples)} 个坏例子到 {output_file}"

    def _extract_error_patterns(self, error_messages: List[str]) -> Dict[str, int]:
        """提取错误模式"""
        patterns = Counter()

        for msg in error_messages:
            if not msg:
                continue

            # 常见错误关键词
            if "timeout" in msg.lower():
                patterns["timeout"] += 1
            elif "permission" in msg.lower() or "access" in msg.lower():
                patterns["permission/access"] += 1
            elif "not found" in msg.lower() or "missing" in msg.lower():
                patterns["not found/missing"] += 1
            elif "connection" in msg.lower() or "network" in msg.lower():
                patterns["connection/network"] += 1
            elif "syntax" in msg.lower() or "parse" in msg.lower():
                patterns["syntax/parse"] += 1
            elif "memory" in msg.lower() or "out of memory" in msg.lower():
                patterns["memory"] += 1
            else:
                patterns["other"] += 1

        return dict(patterns)

    def _extract_keywords(self, texts: List[str]) -> Dict[str, int]:
        """提取关键词"""
        # 简单的关键词提取
        common_words = {"的", "是", "在", "有", "和", "与", "或", "但", "如果", "那么", "the", "is", "at", "which", "on", "and", "or", "but", "if", "then"}
        keywords = Counter()

        for text in texts:
            words = text.lower().split()
            for word in words:
                if len(word) > 3 and word not in common_words:
                    keywords[word] += 1

        return dict(keywords.most_common(20))

    def _calculate_time_stats(self, times: List[float]) -> Dict[str, float]:
        """计算时间统计"""
        if not times:
            return {}

        return {
            "mean": np.mean(times),
            "median": np.median(times),
            "min": min(times),
            "max": max(times),
            "std": np.std(times)
        }

    def _classify_task_types(self, descriptions: List[str]) -> Dict[str, int]:
        """分类任务类型"""
        task_types = Counter()

        for desc in descriptions:
            desc_lower = desc.lower()

            if any(word in desc_lower for word in ["create", "build", "make", "生成", "创建", "构建"]):
                task_types["creation"] += 1
            elif any(word in desc_lower for word in ["fix", "repair", "debug", "修复", "调试", "解决"]):
                task_types["fixing"] += 1
            elif any(word in desc_lower for word in ["analyze", "review", "check", "分析", "检查", "审查"]):
                task_types["analysis"] += 1
            elif any(word in desc_lower for word in ["optimize", "improve", "enhance", "优化", "改进", "增强"]):
                task_types["optimization"] += 1
            elif any(word in desc_lower for word in ["test", "verify", "validate", "测试", "验证"]):
                task_types["testing"] += 1
            else:
                task_types["other"] += 1

        return dict(task_types)"}

