from fastapi.responses import JSONResponse
from typing import List, Optional, Dict, Any
from datetime import datetime
import json

from models import PlannerRecord, AnalysisFilter, PlannerInput, PlannerOutput, PlannerStatus
from database import PlannerDatabase
from planner_logger import PlannerLogger
from analyzer import PlannerAnalyzer

app = FastAPI(title="Planner Logger API", description="Planner 输入输出记录和分析系统")

# 初始化组件
db = PlannerDatabase()
logger = PlannerLogger()
analyzer = PlannerAnalyzer()

@app.post("/records/start")
async def start_record(
    task_description: str,
    context: Optional[Dict[str, Any]] = None,
    constraints: Optional[List[str]] = None,
    user_preferences: Optional[Dict[str, Any]] = None,
    session_id: Optional[str] = None
):
    """开始记录一个新的 planner 执行"""
    try:
        record_id = logger.log_input(
            task_description=task_description,
            context=context,
            constraints=constraints,
            user_preferences=user_preferences,
            session_id=session_id
        )
        return {"record_id": record_id, "status": "started"}
    except Exception as e:
        raise HTTPException(status_code=500, detail=str(e))

@app.post("/records/{record_id}/complete")
async def complete_record(
    record_id: str,
    plan_steps: List[Dict[str, Any]],
    estimated_duration: Optional[float] = None,
    confidence_score: Optional[float] = None,
    reasoning: Optional[str] = None,
    alternatives: Optional[List[Dict[str, Any]]] = None,
    execution_time: Optional[float] = None,
    status: PlannerStatus = PlannerStatus.SUCCESS
):
    """完成记录，添加输出数据"""
    try:
        success = logger.log_output(
            record_id=record_id,
            plan_steps=plan_steps,
            estimated_duration=estimated_duration,
            confidence_score=confidence_score,
            reasoning=reasoning,
            alternatives=alternatives,
            execution_time=execution_time,
            status=status
        )

        if not success:
            raise HTTPException(status_code=404, detail="Record not found")

        return {"record_id": record_id, "status": "completed"}
    except Exception as e:
        raise HTTPException(status_code=500, detail=str(e))

@app.post("/records/{record_id}/error")
async def record_error(
    record_id: str,
    error_message: str,
    execution_time: Optional[float] = None
):
    """记录错误"""
    try:
        success = logger.log_error(record_id, error_message, execution_time)
        if not success:
            raise HTTPException(status_code=404, detail="Record not found")

        return {"record_id": record_id, "status": "error_recorded"}
    except Exception as e:
        raise HTTPException(status_code=500, detail=str(e))

@app.get("/records/{record_id}")
async def get_record(record_id: str):
    """获取单个记录"""
    try:
        record = db.get_record(record_id)
        if not record:
            raise HTTPException(status_code=404, detail="Record not found")

        return record.model_dump()
    except Exception as e:
        raise HTTPException(status_code=500, detail=str(e))

@app.get("/records")
async def get_records(
    status: Optional[PlannerStatus] = None,
    start_date: Optional[datetime] = None,
    end_date: Optional[datetime] = None,
    tags: Optional[List[str]] = Query(None),
    limit: int = Query(100, le=1000),
    offset: int = Query(0, ge=0)
):
    """获取记录列表"""
    try:
        filter_obj = AnalysisFilter(
            status=status,
            start_date=start_date,
            end_date=end_date,
            tags=tags
        )

        records = db.get_records(filter_obj, limit=limit, offset=offset)
        return {
            "records": [record.model_dump() for record in records],
            "total": len(records),
            "limit": limit,
            "offset": offset
        }
    except Exception as e:
        raise HTTPException(status_code=500, detail=str(e))

@app.post("/records/{record_id}/feedback")
async def add_feedback(record_id: str, feedback: Dict[str, Any]):
