@app.post("/records/{record_id}/tags")
async def add_tags(record_id: str, tags: List[str]):
    """添加标签"""
    try:
        success = logger.add_tags(record_id, tags)
        if not success:
            raise HTTPException(status_code=404, detail="Record not found")

        return {"record_id": record_id, "status": "tags_added"}
    except Exception as e:
        raise HTTPException(status_code=500, detail=str(e))

@app.get("/analysis/failures")
async def analyze_failures(limit: int = Query(100, le=500)):
    """分析失败模式"""
    try:
        analysis = analyzer.analyze_failure_patterns(limit)
        return analysis
    except Exception as e:
        raise HTTPException(status_code=500, detail=str(e))

@app.get("/analysis/success")
