@app.get("/statistics")
async def get_statistics():
    """获取统计信息"""
    try:
        stats = db.get_statistics()
        return stats
    except Exception as e:
        raise HTTPException(status_code=500, detail=str(e))

@app.get("/export/bad-examples")
async def export_bad_examples(limit: int = Query(50, le=200)):
    """导出坏例子"""
    try:
        filename = f"bad_examples_{datetime.now().strftime('%Y%m%d_%H%M%S')}.json"
        message = analyzer.export_bad_examples(filename, limit)
        return {"message": message, "filename": filename}
    except Exception as e:
        raise HTTPException(status_code=500, detail=str(e))

