                            <p><strong>任务描述:</strong> ${record.input_data.task_description}</p>
                            ${record.input_data.context ? 
                                `<p><strong>上下文:</strong><br><pre>${JSON.stringify(record.input_data.context, null, 2)}</pre></p>` : ''}
                            ${record.input_data.constraints ? 
                                `<p><strong>约束条件:</strong><br><ul>${record.input_data.constraints.map(c => `<li>${c}</li>`).join('')}</ul></p>` : ''}
                        </div>
                        <div class="col-md-6">
                            <h6>执行信息</h6>
                            <p><strong>状态:</strong> <span class="status-${record.status}">${record.status}</span></p>
                            <p><strong>执行时间:</strong> ${record.execution_time ? record.execution_time.toFixed(1) + 's' : 'N/A'}</p>
                            <p><strong>创建时间:</strong> ${new Date(record.created_at).toLocaleString()}</p>
                            ${record.tags ? `<p><strong>标签:</strong> ${record.tags.join(', ')}</p>` : ''}
                        </div>
                    </div>
                `;

                if (record.output_data) {
                    html += `
                        <h6 class="mt-3">输出信息</h6>
                        <p><strong>预估时长:</strong> ${record.output_data.estimated_duration || 'N/A'} 分钟</p>
                        ${record.output_data.confidence_score ? 
                            `<p><strong>置信度:</strong> ${(record.output_data.confidence_score * 100).toFixed(0)}%</p>` : ''}
                        ${record.output_data.reasoning ? 
                            `<p><strong>推理过程:</strong><br>${record.output_data.reasoning}</p>` : ''}
                        ${record.output_data.plan_steps ? 
                            `<h6>计划步骤:</h6><div class="step-list">${record.output_data.plan_steps.map(step => 
                                `<div class="step-item"><strong>步骤 ${step.step}:</strong> ${step.action} (${step.duration || 'N/A'}${step.duration ? '小时' : ''})<br><small>${step.description || ''}</small></div>`
                            ).join('')}</div>` : ''}
                    `;
                }

                if (record.error_message) {
                    html += `
                        <h6 class="mt-3">错误信息</h6>
                        <div class="alert alert-danger">${record.error_message}</div>
                    `;
                }

                if (record.feedback) {
                    html += `
                        <h6 class="mt-3">用户反馈</h6>
                        <p><strong>评分:</strong> ${record.feedback.user_rating || 'N/A'}/5</p>
                        ${record.feedback.comments ? `<p><strong>评论:</strong> ${record.feedback.comments}</p>` : ''}
                        ${record.feedback.suggestions ? 
                            `<p><strong>建议:</strong><br><ul>${record.feedback.suggestions.map(s => `<li>${s}</li>`).join('')}</ul></p>` : ''}
                    `;
                }

                document.getElementById('recordDetails').innerHTML = html;
                new bootstrap.Modal(document.getElementById('recordModal')).show();
            } catch (error) {
                console.error('加载记录详情失败:', error);
                showError('加载记录详情失败');
            }
        }

        // 刷新记录
        function refreshRecords() {
            loadRecords();
            loadDashboard();
        }

        // 导出坏例子
        async function exportBadExamples() {
            try {
                const response = await fetch(`${API_BASE}/export/bad-examples?limit=50`);
                const result = await response.json();

                if (result.filename) {
                    // 下载文件
                    const downloadResponse = await fetch(`${API_BASE}/export/bad-examples?limit=50`);
                    const blob = await downloadResponse.blob();
                    const url = window.URL.createObjectURL(blob);
                    const a = document.createElement('a');
                    a.href = url;
                    a.download = result.filename;
                    a.click();
                    window.URL.revokeObjectURL(url);

                    showSuccess(`已导出坏例子到 ${result.filename}`);
                }
            } catch (error) {
                console.error('导出失败:', error);
                showError('导出坏例子失败');
            }
        }

        // 显示错误消息
        function showError(message) {
            const alert = document.createElement('div');
            alert.className = 'alert alert-danger alert-dismissible fade show position-fixed top-0 start-50 translate-middle-x mt-3';
            alert.style.zIndex = '9999';
            alert.innerHTML = `
                ${message}
                <button type="button" class="btn-close" data-bs-dismiss="alert"></button>
            `;
            document.body.appendChild(alert);

            setTimeout(() => {
                alert.remove();
            }, 5000);
        }

        // 显示成功消息
        function showSuccess(message) {
            const alert = document.createElement('div');
            alert.className = 'alert alert-success alert-dismissible fade show position-fixed top-0 start-50 translate-middle-x mt-3';
            alert.style.zIndex = '9999';
            alert.innerHTML = `
                ${message}
                <button type="button" class="btn-close" data-bs-dismiss="alert"></button>
            `;
            document.body.appendChild(alert);

            setTimeout(() => {
                alert.remove();
            }, 3000);
        }

        // 定期刷新数据
        setInterval(loadDashboard, 30000); // 每30秒刷新一次
    </script>
