---
import Layout from "../../layouts/Layout.astro";
import { getCollection, getEntry } from "astro:content";
import { extractTaskName } from "../../utils/taskUtils";

export async function getStaticPaths() {
  const tasks = await getCollection("bench");
  return tasks.map((task) => ({
    params: { slug: task.slug },
    props: { task },
  }));
}

const { task } = Astro.props;
const { Content } = await task.render();

const taskNumber = parseInt(task.slug.replace("task_", ""));
const taskTitle = task.data.title || extractTaskName(taskNumber);
const title = `${taskTitle} - InnovatorBench`;
const description =
  task.data.description ||
  `Detailed information about ${taskTitle} from the InnovatorBench benchmark suite.`;

// Get task metadata
const research_tags = task.data.research_tags || [];
const modality_tags = task.data.modality_tags || [];
const application_tags = task.data.application_tags || [];
const creator = task.data.creator || "admin";
const created_time = task.data.created_time || "";
---

<Layout {title} {description}>
  <div class="max-w-5xl mx-auto px-6 py-16">
    <!-- Breadcrumb -->
    <div class="mb-8">
      <a
        href="/tasks"
        class="inline-flex items-center gap-2 text-slate-400 hover:text-blue-400 transition-colors duration-200 font-medium"
      >
        <svg
          class="w-4 h-4"
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
        >
          <path
            stroke-linecap="round"
            stroke-linejoin="round"
            stroke-width="2"
            d="M15 19l-7-7 7-7"></path>
        </svg>
        Back to Tasks
      </a>
    </div>

    <!-- Task Header -->
    <div class="mb-8">
      <h1 class="text-5xl font-bold text-slate-100 mb-6 tracking-tight">
        {taskTitle}
      </h1>

      <!-- Tags Section -->
      {
        (research_tags.length > 0 ||
          modality_tags.length > 0 ||
          application_tags.length > 0) && (
          <div class="flex flex-wrap gap-3 mb-6">
            {research_tags &&
              research_tags.length > 0 &&
              research_tags.map((tag) => (
                <span class="inline-flex items-center px-4 py-2 text-sm font-medium rounded-full bg-blue-500/15 text-blue-400 border border-blue-500/25">
                  {tag}
                </span>
              ))}
            {modality_tags &&
              modality_tags.length > 0 &&
              modality_tags.map((tag) => (
                <span class="inline-flex items-center px-4 py-2 text-sm font-medium rounded-full bg-orange-500/15 text-orange-400 border border-orange-500/25">
                  {tag}
                </span>
              ))}
            {application_tags &&
              application_tags.length > 0 &&
              application_tags.map((tag) => (
                <span class="inline-flex items-center px-4 py-2 text-sm font-medium rounded-full bg-green-500/15 text-green-400 border border-green-500/25">
                  {tag}
                </span>
              ))}
          </div>
        )
      }

      <!-- Meta Information -->
      <div
        class="flex items-center justify-between text-slate-400 text-sm border-b border-slate-700/50 pb-6"
      >
        <div class="flex items-center gap-2">
          <svg
            class="w-4 h-4"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              stroke-linecap="round"
              stroke-linejoin="round"
              stroke-width="2"
              d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"
            ></path>
          </svg>
          <span
            ><strong class="text-slate-300">Created by:</strong> {creator}</span
          >
        </div>
        {
          created_time && (
            <div class="flex items-center gap-2">
              <svg
                class="w-4 h-4"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  stroke-linecap="round"
                  stroke-linejoin="round"
                  stroke-width="2"
                  d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z"
                />
              </svg>
              <span class="font-medium">{created_time}</span>
            </div>
          )
        }
      </div>
    </div>

    <!-- Task Content -->
    <article class="markdown-body">
      <Content />
    </article>
  </div>

  <style>
    /* Task-specific styles */
    .task-header {
      margin-bottom: 2rem;
    }
  </style>
</Layout>
