@import "tailwindcss";

/* Custom CSS Variables for consistent theming */
:root {
  --page-bg: #040810;
  --surface: rgba(12, 20, 38, 0.7);
  --surface-strong: rgba(16, 28, 52, 0.85);
  --glass-border: rgba(120, 178, 255, 0.2);
  --primary: #66b8ff;
  --primary-strong: #4da3ff;
  --secondary: #7b7fff;
  --accent: #ff8a65;
  --text-high: #f5f7ff;
  --text-mid: #c3c9d8;
  --text-low: #8892b0;
  --card-shadow: 0 18px 40px rgba(2, 9, 26, 0.45);
  --font-sans: "Inter", "Segoe UI", "Helvetica Neue", Arial, sans-serif;
}

/* Base styles */
* {
  box-sizing: border-box;
}

html {
  scroll-behavior: smooth;
}

body {
  margin: 0;
  min-height: 100vh;
  background: radial-gradient(circle at top right, rgba(88, 132, 255, 0.32), transparent 55%),
    radial-gradient(circle at 15% 20%, rgba(255, 138, 101, 0.18), transparent 45%),
    var(--page-bg);
  background-attachment: fixed;
  color: var(--text-high);
  font-family: var(--font-sans);
  line-height: 1.6;
  -webkit-font-smoothing: antialiased;
  padding-bottom: 4rem;
  padding-top: 64px;
}

a {
  color: inherit;
  text-decoration: none;
}

/* Navigation styles - keeping custom nav since it's complex */
.nav {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 100;
  backdrop-filter: blur(16px);
  background: rgba(4, 8, 16, 0.88);
  border-bottom: 1px solid rgba(102, 184, 255, 0.14);
  box-shadow: 0 10px 30px rgba(2, 9, 26, 0.4);
}

.nav__inner {
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0.85rem 1.5rem;
}

.brand {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  font-weight: 600;
  letter-spacing: 0.04em;
}

.brand__logo {
  height: 32px;
  width: auto;
  display: block;
}

.brand__symbol {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  width: 34px;
  height: 34px;
  border-radius: 10px;
  background: linear-gradient(135deg, rgba(102, 184, 255, 0.9), rgba(123, 127, 255, 0.9));
  color: var(--page-bg);
  font-weight: 700;
  box-shadow: 0 12px 25px rgba(77, 163, 255, 0.35);
}

.brand__text {
  display: flex;
  flex-direction: column;
  font-size: 0.85rem;
  text-transform: uppercase;
  color: var(--text-mid);
}

.brand__text strong {
  color: var(--text-high);
  font-size: 1.1rem;
  letter-spacing: 0.06em;
}

.nav__links {
  display: flex;
  align-items: center;
  list-style: none;
  margin: 0;
  padding: 0;
  gap: 1rem;
}

.nav__links > li {
  position: relative;
}

.nav__link {
  display: inline-flex;
  align-items: center;
  gap: 0.35rem;
  padding: 0.55rem 0.8rem;
  border-radius: 999px;
  color: var(--text-mid);
  transition: color 0.18s ease, background 0.18s ease, box-shadow 0.18s ease;
}

.nav__link:hover,
.nav__link:focus-visible {
  color: var(--text-high);
  background: rgba(102, 184, 255, 0.14);
  box-shadow: 0 6px 18px rgba(77, 163, 255, 0.25);
}

.nav__link--active {
  color: var(--primary);
  background: rgba(102, 184, 255, 0.18);
  box-shadow: 0 6px 18px rgba(77, 163, 255, 0.3);
  font-weight: 600;
}

.nav__link--active:hover {
  color: var(--primary-strong);
  background: rgba(102, 184, 255, 0.22);
  box-shadow: 0 8px 22px rgba(77, 163, 255, 0.35);
}

.nav__dropdown {
  position: absolute;
  top: calc(100% + 0.5rem);
  left: 0;
  min-width: 240px;
  background: var(--surface-strong);
  border: 1px solid var(--glass-border);
  border-radius: 18px;
  padding: 0.8rem 0.6rem;
  box-shadow: 0 20px 40px rgba(2, 9, 26, 0.55);
  opacity: 0;
  visibility: hidden;
  transform: translateY(-6px);
  transition: opacity 0.18s ease, transform 0.18s ease, visibility 0.18s;
}

.nav__links > li:hover .nav__dropdown,
.nav__links > li:focus-within .nav__dropdown {
  opacity: 1;
  visibility: visible;
  transform: translateY(0);
}

.nav__dropdown a {
  display: block;
  padding: 0.5rem 0.7rem;
  border-radius: 12px;
  color: var(--text-mid);
  transition: background 0.18s ease, color 0.18s ease;
  font-size: 0.95rem;
}

.nav__dropdown a:hover,
.nav__dropdown a:focus-visible {
  background: rgba(102, 184, 255, 0.12);
  color: var(--text-high);
}

/* Responsive navigation */
@media (max-width: 960px) {
  .nav__inner {
    flex-wrap: wrap;
    gap: 1rem;
  }
}

@media (max-width: 840px) {
  .nav__links {
    width: 100%;
    flex-wrap: wrap;
    justify-content: center;
  }
}

@media (max-width: 600px) {
  body {
    padding-bottom: 3rem;
  }

  .nav__inner {
    padding: 0.75rem 1rem;
  }
}

/* Markdown content base styles */
.markdown-body {
  line-height: 1.7;
  color: var(--text-high);
  font-size: 1rem;
}

.markdown-body h1,
.markdown-body h2,
.markdown-body h3,
.markdown-body h4,
.markdown-body h5,
.markdown-body h6 {
  margin-top: 2rem;
  margin-bottom: 1rem;
  font-weight: 600;
  line-height: 1.3;
  color: var(--text-high);
}

.markdown-body h1 {
  font-size: 2.5rem;
  margin-top: 0;
  border-bottom: 2px solid var(--glass-border);
  padding-bottom: 0.5rem;
  color: #ffffff;
}

.markdown-body h2 {
  font-size: 2rem;
  border-bottom: 1px solid var(--glass-border);
  padding-bottom: 0.3rem;
}

.markdown-body h3 {
  font-size: 1.5rem;
}

.markdown-body h4 {
  font-size: 1.25rem;
}

.markdown-body h5 {
  font-size: 1.1rem;
}

.markdown-body h6 {
  font-size: 1rem;
  color: var(--text-mid);
}

.markdown-body p {
  margin-bottom: 1.25rem;
  color: var(--text-mid);
}

.markdown-body ul,
.markdown-body ol {
  margin-bottom: 1.25rem;
  padding-left: 2rem;
}

/* 有序列表样式 - 数字编号 */
.markdown-body ol {
  list-style-type: decimal;
  counter-reset: list-counter;
}

.markdown-body ol li {
  list-style-type: decimal;
  position: relative;
}

.markdown-body ol li::marker {
  color: var(--text-high);
  font-weight: 600;
}

/* 无序列表样式 - 点状编号 */
.markdown-body ul {
  list-style-type: disc;
}

.markdown-body ul li {
  list-style-type: disc;
  position: relative;
}

.markdown-body ul li::marker {
  color: var(--text-high);
}

/* 嵌套列表样式 */
.markdown-body ul ul {
  list-style-type: circle;
  margin-top: 0.5rem;
}

.markdown-body ul ul ul {
  list-style-type: square;
}

.markdown-body ol ol {
  list-style-type: lower-alpha;
  margin-top: 0.5rem;
}

.markdown-body ol ol ol {
  list-style-type: lower-roman;
}

.markdown-body li {
  margin-bottom: 0.5rem;
  color: var(--text-mid);
  padding-left: 0.5rem;
}

.markdown-body li strong {
  color: var(--text-high);
}

.markdown-body blockquote {
  margin: 1.5rem 0;
  padding: 1rem 1.5rem;
  border-left: 4px solid var(--primary);
  background: rgba(102, 184, 255, 0.05);
  border-radius: 0 8px 8px 0;
}

.markdown-body blockquote p {
  margin-bottom: 0;
  font-style: italic;
}

.markdown-body hr {
  border: none;
  height: 1px;
  background: var(--glass-border);
  margin: 2rem 0;
}

.markdown-body table {
  width: 100%;
  border-collapse: collapse;
  margin: 1.5rem 0;
  background: var(--surface);
  border-radius: 8px;
  overflow: hidden;
}

.markdown-body th,
.markdown-body td {
  padding: 0.75rem 1rem;
  text-align: left;
  border-bottom: 1px solid var(--glass-border);
}

.markdown-body th {
  background: var(--surface-strong);
  font-weight: 600;
  color: var(--text-high);
}

.markdown-body td {
  color: var(--text-mid);
}

/* Markdown content link styles */
.markdown-body a {
  color: var(--primary);
  text-decoration: none;
  font-weight: 500;
  border-bottom: 1px solid rgba(102, 184, 255, 0.3);
  transition: color 0.18s ease, border-color 0.18s ease;
}

.markdown-body a:hover,
.markdown-body a:focus {
  color: var(--primary-strong);
  border-bottom-color: var(--primary-strong);
}

.markdown-body a:visited {
  color: var(--secondary);
  border-bottom-color: rgba(123, 127, 255, 0.3);
}

/* Code block styles for Shiki syntax highlighting */
.markdown-body pre {
  background: var(--surface-strong) !important;
  border: 1px solid var(--glass-border);
  border-radius: 12px;
  padding: 3rem 1.5rem 1.5rem 1.5rem;
  margin: 1.5rem 0;
  overflow-x: auto;
  box-shadow: var(--card-shadow);
  backdrop-filter: blur(20px);
  position: relative;
}

.markdown-body pre code {
  background: transparent !important;
  padding: 0 !important;
  border-radius: 0 !important;
  font-size: 0.9rem;
  line-height: 1.6;
  font-family: 'JetBrains Mono', 'Fira Code', 'Monaco', 'Consolas', monospace;
}

/* 行内代码样式 - 不在 pre 标签内的 code */
.markdown-body code:not(pre code) {
  color: var(--text-high);
  background: rgba(102, 184, 255, 0.1);
  padding: 0.2rem 0.4rem;
  border-radius: 6px;
  font-size: 0.9em;
  font-family: 'JetBrains Mono', 'Fira Code', 'Monaco', 'Consolas', monospace;
  /* border: 1px solid rgba(102, 184, 255, 0.2); */
}

/* 代码块内的代码样式 - 在 pre 标签内的 code */
.markdown-body pre code {
  color: inherit;
  background: transparent;
  padding: 0;
  border: none;
  border-radius: 0;
  font-size: 0.9rem;
  line-height: 1.6;
  font-family: 'JetBrains Mono', 'Fira Code', 'Monaco', 'Consolas', monospace;
}

/* Language label for code blocks - moved to left side */
.markdown-body pre[data-language]::before {
  content: attr(data-language);
  position: absolute;
  top: 0.75rem;
  left: 1rem;
  font-size: 0.75rem;
  color: var(--text-low);
  text-transform: uppercase;
  letter-spacing: 0.05em;
  font-weight: 600;
  padding: 0.25rem 0.5rem;
  border-radius: 4px;
}

/* Copy button for code blocks - styles for real HTML button */
.markdown-body pre .copy-button {
  position: absolute;
  top: 0.75rem;
  right: 1rem;
  width: 1.5rem;
  height: 1.5rem;
  cursor: pointer;
  padding: 0.25rem;
  border-radius: 4px;
  transition: all 0.18s ease;
  user-select: none;
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--text-low);
}

.markdown-body pre .copy-button:hover {
  background-color: rgba(102, 184, 255, 0.2);
  border-color: rgba(102, 184, 255, 0.3);
  color: var(--text-high);
}

.markdown-body pre .copy-button svg {
  width: 1rem;
  height: 1rem;
  stroke: currentColor;
}

/* 代码块行号样式 */
pre.line-numbers {
  position: relative;
  padding-left: 3.5em;
  counter-reset: line;
}

pre.line-numbers .line {
  counter-increment: line;
  position: relative;
}

pre.line-numbers .line::before {
  content: counter(line);
  position: absolute;
  left: -3.5em;
  width: 3em;
  text-align: right;
  color: #6b7280;
  font-size: 0.875em;
  line-height: inherit;
  user-select: none;
  pointer-events: none;
  padding-right: 1em;
}

/* 深色主题下的行号颜色调整 */
pre.line-numbers[data-theme="github-dark"] .line::before {
  color: #6b7280;
}

/* 浅色主题下的行号颜色调整 */
pre.line-numbers[data-theme="github-light"] .line::before {
  color: #9ca3af;
}

/* 确保代码块内容不被行号遮挡 */
pre.line-numbers code {
  display: block;
}

/* Custom utility classes that complement Tailwind */
.glass-surface {
  background: var(--surface);
  border: 1px solid var(--glass-border);
  backdrop-filter: blur(16px);
}

.glass-surface-strong {
  background: var(--surface-strong);
  border: 1px solid var(--glass-border);
  backdrop-filter: blur(20px);
}

.card-shadow {
  box-shadow: var(--card-shadow);
}

.text-gradient {
  background: linear-gradient(135deg, var(--primary), var(--secondary));
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

/* Animation utilities */
.animate-float {
  animation: float 6s ease-in-out infinite;
}

@keyframes float {
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-10px);
  }
}

.animate-glow {
  animation: glow 2s ease-in-out infinite alternate;
}

@keyframes glow {
  from {
    box-shadow: 0 0 20px rgba(102, 184, 255, 0.3);
  }
  to {
    box-shadow: 0 0 30px rgba(102, 184, 255, 0.6);
  }
}

