	// Fullpage functionality
	let currentSection = 0;
	const totalSections = 3;
	let isScrolling = false;

	function scrollToSection(sectionIndex) {
		if (isScrolling) return;

		isScrolling = true;
		currentSection = sectionIndex;

		const container = document.getElementById('fullpage-container');
		container.style.transform = `translateY(-${sectionIndex * 100}vh)`;

		// Update navigation dots
		document.querySelectorAll('.nav-dot').forEach((dot, index) => {
			dot.classList.toggle('active', index === sectionIndex);
		});

		setTimeout(() => {
			isScrolling = false;
		}, 800);
	}

	// Wheel event for scrolling
	function handleWheel(e) {
		if (isScrolling) return;

		e.preventDefault();

		if (e.deltaY > 0 && currentSection < totalSections - 1) {
			// Scroll down
			scrollToSection(currentSection + 1);
		} else if (e.deltaY < 0 && currentSection > 0) {
			// Scroll up
			scrollToSection(currentSection - 1);
		}
	}

	// Keyboard navigation
	function handleKeydown(e) {
		if (isScrolling) return;

		if (e.key === 'ArrowDown' && currentSection < totalSections - 1) {
			scrollToSection(currentSection + 1);
		} else if (e.key === 'ArrowUp' && currentSection > 0) {
			scrollToSection(currentSection - 1);
		}
	}

	// Touch events for mobile
	let touchStartY = 0;
	function handleTouchStart(e) {
		touchStartY = e.touches[0].clientY;
	}

	function handleTouchEnd(e) {
		if (isScrolling) return;

		const touchEndY = e.changedTouches[0].clientY;
		const diff = touchStartY - touchEndY;

		if (Math.abs(diff) > 50) { // Minimum swipe distance
			if (diff > 0 && currentSection < totalSections - 1) {
				// Swipe up (scroll down)
				scrollToSection(currentSection + 1);
			} else if (diff < 0 && currentSection > 0) {
				// Swipe down (scroll up)
				scrollToSection(currentSection - 1);
			}
		}
	}

	// Text rotation animation with magical effects
	const rotatingText = document.getElementById('rotating-text');
