const description =
	"AI-Innovator is the home of InnovatorBench, a benchmark suite for evaluating research-ready AI agents. Explore docs, registry, leaderboard, news, and related papers.";
---

<Layout {title} {description}>
	<!-- Page Navigation Indicators -->
	<div class="fixed right-8 top-1/2 transform -translate-y-1/2 z-50 flex flex-col gap-3">
		<button class="page-indicator active" data-page="0" aria-label="Go to Hero section">
			<span class="sr-only">Hero</span>
		</button>
		<button class="page-indicator" data-page="1" aria-label="Go to Interface section">
			<span class="sr-only">Interface</span>
		</button>
		<button class="page-indicator" data-page="2" aria-label="Go to Leaderboard section">
			<span class="sr-only">Leaderboard</span>
		</button>
	</div>



	<!-- Page Container -->
	<div id="page-container" class="overflow-hidden">
		<!-- Hero Section -->
		<section class="page-section h-screen flex items-center justify-center" data-page="0" id="hero">
			<div class="max-w-7xl mx-auto px-6 grid grid-cols-1 lg:grid-cols-2 gap-16 items-center w-full">
				<!-- Hero Content -->
				<div class="flex flex-col gap-8 text-center lg:text-justify lg:pr-24 lg:max-w-none lg:w-full">
					<h1 class="text-5xl lg:text-6xl lg:text-justify font-bold text-slate-100 leading-tight tracking-tight lg:whitespace-nowrap">
						<!-- <div>Accelerate innovative LLM Research with</div> -->
						<div>Accelerate innovative</div>
						<div><span id="rotating-text" class="bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent inline-block w-[380px] lg:w-[700px] text-left">LLM research</span></div>
						<!-- <div>with</div> -->
						<div>
							with
							<span class="bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
								agent-ready
							</span>
						</div>
						<div>
							<span class="bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
								infrastructure
							</span>
						</div>
					</h1>
					<p class="text-xl text-slate-300 leading-relaxed w-[300px] lg:w-[600px] lg:text-justify">
						AI-innovator suite brings together popular research topics, evaluation
						tooling, and human-agent interfaces to measure an agent's
						ability to produce novel research-grade insights.
					</p>
					<div class="flex flex-col sm:flex-row gap-4 justify-center lg:justify-start">
						<a 
							href="/docs" 
							class="inline-flex items-center justify-center px-8 py-4 text-lg font-semibold text-slate-900 bg-gradient-to-r from-blue-400 to-purple-400 rounded-full hover:from-blue-300 hover:to-purple-300 transform hover:-translate-y-1 transition-all duration-300 shadow-lg hover:shadow-xl"
						>
							Explore Docs
						</a>
						<a 
							href="/leaderboard" 
							class="inline-flex items-center justify-center px-8 py-4 text-lg font-semibold text-slate-100 bg-slate-800/70 backdrop-blur-sm border border-blue-500/20 rounded-full hover:bg-blue-500/10 hover:border-blue-400/40 transform hover:-translate-y-1 transition-all duration-300"
