<script>
	// Page navigation state
	let currentPage = 0;
	const totalPages = 3;
	let isTransitioning = false;

	// Text rotation animation with magical effects
	const rotatingText = document.getElementById('rotating-text');
	const texts = [
		'Loooong-Horizon Task',
		'LLM research',
		'Data Construction', 
		'Data Filtering',
		'Data Augmentation',
		'Reward Design',
		'Loss Design',
		'Scaffold Construction',
	];

	let currentTextIndex = 0;

	function createSparkles() {
		const sparkleContainer = document.createElement('div');
		sparkleContainer.className = 'sparkle-container';
		rotatingText.appendChild(sparkleContainer);

		for (let i = 0; i < 6; i++) {
			const sparkle = document.createElement('div');
			sparkle.className = 'sparkle';
			sparkle.style.left = Math.random() * 100 + '%';
			sparkle.style.top = Math.random() * 100 + '%';
			sparkle.style.animationDelay = Math.random() * 0.5 + 's';
			sparkleContainer.appendChild(sparkle);
		}

		setTimeout(() => {
			sparkleContainer.remove();
		}, 1000);
	}

	function animateTextChange() {
		// Blur and fade out
		rotatingText.style.filter = 'blur(20px)';
		rotatingText.style.opacity = '0';

		setTimeout(() => {
			// Change text
			currentTextIndex = (currentTextIndex + 1) % texts.length;
			rotatingText.textContent = texts[currentTextIndex];

			// Blur in and fade in
			rotatingText.style.filter = 'blur(0px)';
			rotatingText.style.opacity = '1';
		}, 300);
	}

	// Page navigation functions
	function updatePageIndicators() {
		const indicators = document.querySelectorAll('.page-indicator');
		indicators.forEach((indicator, index) => {
			indicator.classList.toggle('active', index === currentPage);
		});
	}



	function goToPage(pageIndex) {
		if (isTransitioning || pageIndex < 0 || pageIndex >= totalPages || pageIndex === currentPage) {
			return;
		}

		isTransitioning = true;
		const container = document.getElementById('page-container');

		// Calculate transform
		const translateY = -pageIndex * 100;
		container.style.transform = `translateY(${translateY}vh)`;

		currentPage = pageIndex;
		updatePageIndicators();

		// Reset transition flag
		setTimeout(() => {
			isTransitioning = false;
		}, 800);
	}

	function nextPage() {
		if (currentPage < totalPages - 1) {
			goToPage(currentPage + 1);
		}
	}

	function prevPage() {
		if (currentPage > 0) {
			goToPage(currentPage - 1);
		}
	}

	// Initialize page navigation
	document.addEventListener('DOMContentLoaded', () => {
		// Set up page indicators
		const indicators = document.querySelectorAll('.page-indicator');
		indicators.forEach((indicator, index) => {
			indicator.addEventListener('click', () => goToPage(index));
		});



		// Keyboard navigation
		document.addEventListener('keydown', (e) => {
			if (e.key === 'ArrowDown' || e.key === 'PageDown') {
				e.preventDefault();
				nextPage();
			} else if (e.key === 'ArrowUp' || e.key === 'PageUp') {
				e.preventDefault();
				prevPage();
			}
		});

		// Mouse wheel navigation
		let wheelTimeout;
		document.addEventListener('wheel', (e) => {
			e.preventDefault();

			clearTimeout(wheelTimeout);
			wheelTimeout = setTimeout(() => {
				if (e.deltaY > 0) {
					nextPage();
				} else {
					prevPage();
				}
			}, 50);
		}, { passive: false });

		// Touch navigation for mobile
		let touchStartY = 0;
		let touchEndY = 0;

		document.addEventListener('touchstart', (e) => {
			touchStartY = e.changedTouches[0].screenY;
		});

		document.addEventListener('touchend', (e) => {
			touchEndY = e.changedTouches[0].screenY;
			const deltaY = touchStartY - touchEndY;

			if (Math.abs(deltaY) > 50) { // Minimum swipe distance
				if (deltaY > 0) {
					nextPage();
				} else {
					prevPage();
				}
			}
		});

		// Initialize UI state
		updatePageIndicators();
	});

	// Start the text animation after page load
	setTimeout(() => {
		setInterval(animateTextChange, 3500);
	}, 2000);
</script>

<style>
	/* Page container styles */
	#page-container {
		transition: transform 0.8s cubic-bezier(0.25, 0.46, 0.45, 0.94);
		will-change: transform;
	}

	.page-section {
		width: 100vw;
		position: relative;
	}

	/* Page indicators */
	.page-indicator {
		width: 12px;
		height: 12px;
		border-radius: 50%;
		border: 2px solid rgba(148, 163, 184, 0.4);
		background: transparent;
		transition: all 0.3s ease;
		cursor: pointer;
	}

	.page-indicator:hover {
		border-color: rgba(59, 130, 246, 0.6);
		background: rgba(59, 130, 246, 0.2);
		transform: scale(1.2);
	}

	.page-indicator.active {
		border-color: #3b82f6;
		background: #3b82f6;
		box-shadow: 0 0 12px rgba(59, 130, 246, 0.5);
	}

	/* Text rotation styles */
	#rotating-text {
		transition: filter 0.6s ease-in-out, opacity 0.6s ease-in-out;
		position: relative;
		display: inline-block;
	}

	.sparkle-container {
		position: absolute;
		top: 0;
		left: 0;
		width: 100%;
		height: 100%;
		pointer-events: none;
		z-index: 10;
	}

	.sparkle {
		position: absolute;
		width: 4px;
		height: 4px;
		background: linear-gradient(45deg, #66b8ff, #7b7fff);
		border-radius: 50%;
		animation: sparkleAnimation 1s ease-out forwards;
		box-shadow: 0 0 6px rgba(102, 184, 255, 0.8);
	}

	@keyframes sparkleAnimation {
		0% {
			opacity: 0;
			transform: scale(0) rotate(0deg);
		}
		50% {
			opacity: 1;
			transform: scale(1) rotate(180deg);
		}
		100% {
			opacity: 0;
			transform: scale(0) rotate(360deg);
		}
	}



	/* Prevent scrolling */
	html, body {
		overflow: hidden;
		height: 100vh;
	}

	/* Smooth scrolling for fallback */
	@media (prefers-reduced-motion: reduce) {
		#page-container {
			transition: none;
		}

		.page-indicator {
			transition: none;
		}
	}
</style>

