import React, { useState, useEffect } from 'react';
import {
    BarChart,
    Bar,
    XAxis,
    YAxis,
    Tooltip,
    ResponsiveContainer,
    Cell,
    LabelList
} from 'recharts';

const LeaderboardChart = () => {
    // Animation state
    const [animatedData, setAnimatedData] = useState([]);
    const [isAnimating, setIsAnimating] = useState(false);

    // Leaderboard Data
    const data = [
        {
            rank: 1,
            model: 'Claude Sonnet 4',
            organization: 'Anthropic',
            score: 24.54
        },
        {
            rank: 2,
            model: 'ARGO',
            organization: 'SII-GAIR',
            score: 24.01
        },
        {
            rank: 3,
            model: 'GPT-5',
            organization: 'OpenAI',
            score: 12.52
        },
        {
            rank: 4,
            model: 'GLM-4.5',
            organization: 'Zhipu AI',
            score: 13.35
        },
        {
            rank: 5,
            model: 'Kimi-K2-Instruct-0905',
            organization: 'MoonShot',
            score: 5.45
        }
    ];

    // Animation function
    const startAnimation = () => {
        // Initialize with zero scores
        const initialData = data.map(item => ({ ...item, score: 0 }));
        setAnimatedData(initialData);
        setIsAnimating(true);

