    }
}"}

{"file_path":"/data1/wysi/sii-cli-dev/packages/python-sdk/examples/project/script.js","content":"class GomokuGame {
    constructor() {
        this.boardSize = 15;
        this.board = [];
        this.currentPlayer = 'black';
        this.gameOver = false;
        this.moveHistory = [];
        this.winningCells = [];

        this.initializeGame();
        this.bindEvents();
    }

    initializeGame() {
        // åˆå§‹åŒ–æ£‹ç›˜æ•°ç»„
        this.board = Array(this.boardSize).fill(null).map(() => Array(this.boardSize).fill(null));
        this.currentPlayer = 'black';
        this.gameOver = false;
        this.moveHistory = [];
        this.winningCells = [];

        // åˆ›å»ºæ£‹ç›˜DOM
        this.createBoard();
        this.updateUI();
    }

    createBoard() {
        const boardElement = document.getElementById('game-board');
        boardElement.innerHTML = '';

        for (let row = 0; row < this.boardSize; row++) {
            for (let col = 0; col < this.boardSize; col++) {
                const cell = document.createElement('div');
                cell.className = 'cell';
                cell.dataset.row = row;
                cell.dataset.col = col;
                cell.addEventListener('click', () => this.handleCellClick(row, col));
                boardElement.appendChild(cell);
            }
        }
    }

    handleCellClick(row, col) {
        if (this.gameOver || this.board[row][col] !== null) {
            return;
        }

        // æ”¾ç½®æ£‹å­
        this.board[row][col] = this.currentPlayer;
        this.moveHistory.push({ row, col, player: this.currentPlayer });

        // æ›´æ–°UI
        this.renderPiece(row, col, this.currentPlayer);

        // æ£€æŸ¥èƒœè´Ÿ
        if (this.checkWin(row, col)) {
            this.endGame(this.currentPlayer);
            return;
        }

        // æ£€æŸ¥å¹³å±€
        if (this.checkDraw()) {
            this.endGame('draw');
            return;
        }

        // åˆ‡æ¢ç©å®¶
        this.currentPlayer = this.currentPlayer === 'black' ? 'white' : 'black';
        this.updateUI();
    }

    renderPiece(row, col, player) {
        const cell = document.querySelector(`[data-row="${row}"][data-col="${col}"]`);
        const piece = document.createElement('div');
        piece.className = `piece ${player}`;
        cell.appendChild(piece);
        cell.classList.add('disabled');
    }

    checkWin(row, col) {
        const directions = [
            [[0, 1], [0, -1]],   // æ°´å¹³
            [[1, 0], [-1, 0]],   // å‚ç›´
            [[1, 1], [-1, -1]],  // å¯¹è§’çº¿
            [[1, -1], [-1, 1]]   // åå¯¹è§’çº¿
        ];

        for (const direction of directions) {
            const cells = this.getConsecutiveCells(row, col, direction);
            if (cells.length >= 5) {
                this.winningCells = cells;
                return true;
            }
        }

        return false;
    }

    getConsecutiveCells(row, col, direction) {
        const player = this.board[row][col];
        const cells = [[row, col]];

        // æ£€æŸ¥ä¸¤ä¸ªæ–¹å‘
        for (const [dr, dc] of direction) {
            let r = row + dr;
            let c = col + dc;

            while (r >= 0 && r < this.boardSize && c >= 0 && c < this.boardSize && 
                   this.board[r][c] === player) {
                cells.push([r, c]);
                r += dr;
                c += dc;
            }
        }

        return cells;
    }

    checkDraw() {
        return this.moveHistory.length === this.boardSize * this.boardSize;
    }

    endGame(winner) {
        this.gameOver = true;

        // é«˜äº®è·èƒœæ£‹å­
        if (winner !== 'draw') {
            this.highlightWinningCells();
        }

        // æ˜¾ç¤ºæ¸¸æˆç»“æŸç•Œé¢
        this.showGameOverOverlay(winner);
        this.updateUI();
    }

    highlightWinningCells() {
        for (const [row, col] of this.winningCells) {
            const cell = document.querySelector(`[data-row="${row}"][data-col="${col}"]`);
            const piece = cell.querySelector('.piece');
            if (piece) {
                piece.classList.add('winning');
            }
        }
    }

    showGameOverOverlay(winner) {
        const overlay = document.createElement('div');
        overlay.className = 'game-over-overlay';

        const content = document.createElement('div');
        content.className = 'game-over-content';

        const winnerText = document.createElement('div');
        winnerText.className = 'winner-text';

        if (winner === 'draw') {
            winnerText.textContent = 'å¹³å±€ï¼';
        } else {
            const winnerName = winner === 'black' ? 'é»‘æ£‹' : 'ç™½æ£‹';
            winnerText.textContent = `${winnerName} è·èƒœï¼`;
        }

        const playAgainBtn = document.createElement('button');
        playAgainBtn.className = 'play-again-btn';
        playAgainBtn.textContent = 'å†æ¥ä¸€å±€';
        playAgainBtn.addEventListener('click', () => {
            document.body.removeChild(overlay);
            this.restart();
        });

        content.appendChild(winnerText);
        content.appendChild(playAgainBtn);
        overlay.appendChild(content);
        document.body.appendChild(overlay);
    }

    undo() {
        if (this.moveHistory.length === 0 || this.gameOver) {
            return;
        }

        const lastMove = this.moveHistory.pop();
        this.board[lastMove.row][lastMove.col] = null;

        // ç§»é™¤æ£‹å­
        const cell = document.querySelector(`[data-row="${lastMove.row}"][data-col="${lastMove.col}"]`);
        const piece = cell.querySelector('.piece');
        if (piece) {
            cell.removeChild(piece);
        }
        cell.classList.remove('disabled');

        // åˆ‡æ¢å›ä¸Šä¸€ä¸ªç©å®¶
        this.currentPlayer = lastMove.player;
        this.updateUI();
    }

    restart() {
        this.initializeGame();
    }

    updateUI() {
        // æ›´æ–°å½“å‰ç©å®¶æ˜¾ç¤º
        const currentPlayerElement = document.getElementById('current-player');
        currentPlayerElement.textContent = this.currentPlayer === 'black' ? 'é»‘æ£‹' : 'ç™½æ£‹';

        // æ›´æ–°æ¸¸æˆçŠ¶æ€
        const gameStatusElement = document.getElementById('game-status');
        if (this.gameOver) {
            gameStatusElement.textContent = 'æ¸¸æˆç»“æŸ';
        } else {
            gameStatusElement.textContent = 'æ¸¸æˆè¿›è¡Œä¸­';
        }

        // æ›´æ–°æ‚”æ£‹æŒ‰é’®çŠ¶æ€
        const undoBtn = document.getElementById('undo-btn');
        undoBtn.disabled = this.moveHistory.length === 0 || this.gameOver;
    }

    bindEvents() {
        // é‡æ–°å¼€å§‹æŒ‰é’®
        document.getElementById('restart-btn').addEventListener('click', () => {
            this.restart();
        });

        // æ‚”æ£‹æŒ‰é’®
        document.getElementById('undo-btn').addEventListener('click', () => {
            this.undo();
        });

        // é”®ç›˜å¿«æ·é”®
        document.addEventListener('keydown', (e) => {
            if (e.key === 'r' || e.key === 'R') {
                this.restart();
            } else if (e.key === 'z' || e.key === 'Z') {
                this.undo();
            }
        });
    }
}

// é¡µé¢åŠ è½½å®Œæˆååˆå§‹åŒ–æ¸¸æˆ
document.addEventListener('DOMContentLoaded', () => {
    new GomokuGame();
});"}

